  // User management
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    for (const user of this.users.values()) {
      if (user.username === username) return user;
    }
    return undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    for (const user of this.users.values()) {
      if (user.email === email) return user;
    }
    return undefined;
  }

  async getUserByOktaId(oktaUserId: string): Promise<User | undefined> {
    for (const user of this.users.values()) {
      if (user.oktaUserId === oktaUserId) return user;
    }
    return undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const user: User = {
      id: randomUUID(),
      ...insertUser,
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.users.set(user.id, user);
    return user;
  }

  async updateUser(id: string, updateUser: Partial<InsertUser>): Promise<User> {
    const existingUser = this.users.get(id);
    if (!existingUser) {
      throw new Error(`User with id ${id} not found`);
    }

    const updatedUser: User = {
      ...existingUser,
      ...updateUser,
      updatedAt: new Date()
    };

    this.users.set(id, updatedUser);
    return updatedUser;
  }

  // Warehouse management
  async getWarehouses(): Promise<Warehouse[]> {
    return Array.from(this.warehouses.values()).filter(w => w.active);
  }

  async getWarehouse(id: string): Promise<Warehouse | undefined> {
    return this.warehouses.get(id);
  }

  async createWarehouse(insertWarehouse: InsertWarehouse): Promise<Warehouse> {
    const warehouse: Warehouse = {
      id: randomUUID(),
      ...insertWarehouse,
      createdAt: new Date()
    };
    this.warehouses.set(warehouse.id, warehouse);
    return warehouse;
  }

  // Inventory management
  async getInventoryByWarehouse(warehouseId: string): Promise<InventoryItem[]> {
    return Array.from(this.inventoryItems.values())
      .filter(item => item.warehouseId === warehouseId);
  }

  async getInventoryItem(id: string): Promise<InventoryItem | undefined> {
    return this.inventoryItems.get(id);
  }

  async createInventoryItem(insertItem: InsertInventoryItem): Promise<InventoryItem> {
    const item: InventoryItem = {
      id: randomUUID(),
      ...insertItem,
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.inventoryItems.set(item.id, item);
