import { useEffect, useState } from "react";
import { authService, type AuthState } from "@/lib/auth";
import { getOktaConfig, type OktaConfig } from "@/lib/okta-config";
import { generateCodeVerifier, generateCodeChallenge, buildAuthUrl } from "@/lib/auth";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";

interface AuthGuardProps {
  children: React.ReactNode;
  application: 'inventory' | 'jarvis';
  title: string;
  description: string;
  icon: string;
  theme: 'atlas' | 'jarvis';
}

export default function AuthGuard({ 
  children, 
  application, 
  title, 
  description, 
  icon, 
  theme 
}: AuthGuardProps) {
  const [authState, setAuthState] = useState<AuthState>(authService.getState());
  const [isLoading, setIsLoading] = useState(false);
  const [config] = useState<OktaConfig>(getOktaConfig(application));

  useEffect(() => {
    const unsubscribe = authService.subscribe(setAuthState);
    return unsubscribe;
  }, []);

  useEffect(() => {
    // Handle OAuth callback - let backend handle token exchange
    const handleCallback = async () => {
      const urlParams = new URLSearchParams(window.location.search);
      const code = urlParams.get('code');
      const state = urlParams.get('state');
      const storedState = sessionStorage.getItem('oauth_state');
      const codeVerifier = sessionStorage.getItem('code_verifier');

      console.log('Callback debug:', { code, state, storedState, codeVerifier });

      if (code && state === storedState && codeVerifier) {
        setIsLoading(true);
        try {
          const requestBody = {
            code,
            state,
            application,
            redirectUri: config.redirectUri,
            codeVerifier,
          };
          console.log('Sending callback request:', requestBody);

          // Send the authorization code to backend for token exchange
          const response = await fetch('/api/auth/callback', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestBody),
          });

          if (!response.ok) {
            throw new Error('Authentication failed');
          }

          const data = await response.json();
          authService.setIdToken(data.idToken);
          await authService.login(data.idToken, application);

          // Clean up
          sessionStorage.removeItem('oauth_state');
          sessionStorage.removeItem('code_verifier');

          // Force state update
          setAuthState(authService.getState());

          window.history.replaceState({}, document.title, window.location.pathname);
        } catch (error) {
          console.error('Authentication error:', error);
        } finally {
          setIsLoading(false);
        }
      }
    };

    handleCallback();
  }, [config, application]);

  const handleLogin = async () => {
    setIsLoading(true);
    try {
      const codeVerifier = generateCodeVerifier();
      const codeChallenge = await generateCodeChallenge(codeVerifier);
      const state = crypto.randomUUID();

      sessionStorage.setItem('code_verifier', codeVerifier);
      sessionStorage.setItem('oauth_state', state);

      const authUrl = buildAuthUrl(config, codeChallenge, state);
      window.location.href = authUrl;
    } catch (error) {
      console.error('Login error:', error);
      setIsLoading(false);
    }
  };

  const isAppAuthenticated = authState.isAuthenticated && authState.session?.application === application;


  if (isAppAuthenticated) {
    return <>{children}</>;
  }

  const themeClasses = {
    atlas: {
      bg: "bg-white",
      gradient: "bg-gradient-to-r from-blue-600 to-blue-700",
      iconBg: "bg-gradient-to-br from-blue-600 to-blue-700",
      accent: "text-blue-600",
      dots: "bg-blue-600",
    },
    jarvis: {
      bg: "bg-slate-800/90 border-blue-400/30 backdrop-blur-sm",
      gradient: "bg-gradient-to-r from-blue-500 to-cyan-500 text-white",
      iconBg: "bg-gradient-to-br from-blue-500 to-cyan-500",
      accent: "text-blue-400",
      dots: "bg-blue-400",
    },
  };

  const styles = themeClasses[theme];

  return (
    <div className={`fixed inset-0 flex items-center justify-center z-50 ${theme === 'jarvis' ? 'bg-gradient-to-br from-blue-900 via-blue-800 to-blue-900 overflow-hidden' : 'bg-black/50'}`}>
      {/* Enhanced radial spinning background for Jarvis */}
      {theme === 'jarvis' && (
        <div className="absolute inset-0 flex items-center justify-center pointer-events-none overflow-hidden">
          {/* Outer ring with radial lines */}
          <div className="relative w-96 h-96">
            <div className="absolute inset-0 animate-spin" style={{ animationDuration: '8s' }}>
              {Array.from({ length: 24 }).map((_, i) => (
                <div
                  key={i}
                  className="absolute w-1 bg-blue-400/30"
                  style={{
                    height: '48px',
                    left: '50%',
                    top: '0',
                    transformOrigin: '50% 192px',
                    transform: `translateX(-50%) rotate(${i * 15}deg)`,
                  }}
                />
              ))}
            </div>
          </div>

          {/* Middle ring with shorter lines */}
          <div className="absolute relative w-64 h-64">
            <div className="absolute inset-0 animate-spin" style={{ animationDuration: '6s', animationDirection: 'reverse' }}>
              {Array.from({ length: 16 }).map((_, i) => (
                <div
                  key={i}
                  className="absolute w-1 bg-blue-300/40"
                  style={{
                    height: '32px',
                    left: '50%',
                    top: '0',
                    transformOrigin: '50% 128px',
                    transform: `translateX(-50%) rotate(${i * 22.5}deg)`,
                  }}
                />
              ))}
            </div>
          </div>

          {/* Inner ring with accent lines */}
          <div className="absolute relative w-32 h-32">
            <div className="absolute inset-0 animate-spin" style={{ animationDuration: '4s' }}>
              {Array.from({ length: 8 }).map((_, i) => (
                <div
                  key={i}
                  className={`absolute w-1 ${i % 3 === 0 ? 'bg-cyan-400/60' : 'bg-blue-200/50'}`}
                  style={{
                    height: '20px',
                    left: '50%',
                    top: '0',
                    transformOrigin: '50% 64px',
                    transform: `translateX(-50%) rotate(${i * 45}deg)`,
                  }}
                />
              ))}
            </div>
          </div>
        </div>
      )}
      <Card className={`w-full max-w-md mx-4 animate-fade-in relative z-10 ${styles.bg} ${theme === 'jarvis' ? 'shadow-blue-400/20 shadow-2xl' : ''}`}>
        <CardContent className="pt-6">
          <div className="text-center mb-6">
            <div className={`w-16 h-16 ${styles.iconBg} rounded-full flex items-center justify-center mx-auto mb-4 relative`}>
              <i className={`${icon} text-white text-2xl`} />
              {theme === 'jarvis' && (
                <div className="absolute inset-0 w-16 h-16 border-2 border-blue-400/50 rounded-full animate-spin" style={{ animationDuration: '2s' }} />
              )}
            </div>
            <h2 className={`text-2xl font-bold ${theme === 'jarvis' ? 'text-white' : 'text-gray-900'}`}>
              {title}
            </h2>
            <p className={`${styles.accent} mt-2`}>{description}</p>
          </div>

          <div className="space-y-4">
            {isLoading ? (
              <div className="text-center">
                <p className={`text-sm mb-4 ${theme === 'jarvis' ? 'text-blue-300' : 'text-gray-600'}`}>
                  {theme === 'jarvis' ? 'Starting authentication...' : 'Authenticating with enterprise identity provider...'}
                </p>
                <div className="flex items-center justify-center space-x-2">
                  <div className={`w-3 h-3 ${styles.dots}/60 rounded-full`}>
                    <div className={`w-full h-full ${styles.dots} rounded-full animate-ping`} />
                  </div>
                </div>
              </div>
            ) : (
              <Button 
                onClick={handleLogin}
                className={`w-full ${styles.gradient} font-medium hover:opacity-90 transition-opacity shadow-lg`}
                data-testid={`button-login-${application}`}
              >
                {theme === 'jarvis' ? 'Sign in with Okta' : `Continue to ${title}`}
              </Button>
            )}
          </div>

          <div className={`mt-6 text-center text-xs ${theme === 'jarvis' ? 'text-blue-400/80' : 'text-gray-500'}`}>
            <p>Secured by Enterprise SSO</p>
            <p className="mt-1">
              {theme === 'jarvis' ? 'Cross-Interface Access ‚Ä¢ Token Exchange' : 'PKCE ‚Ä¢ OpenID Connect'}
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/TokenSidebar.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { authService } from "@/lib/auth";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { X, Key, Clock, CheckCircle, XCircle, Eye, EyeOff, Copy } from "lucide-react";

interface TokenSidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

interface SessionData {
  sessions: Array<{
    id: string;
    application: string;
    createdAt: string;
    expiresAt: string;
    idTokenPreview: string;
  }>;
  tokenExchangeHistory: Array<{
    id: string;
    fromApp: string;
    toApp: string;
    success: boolean;
    createdAt: string;
    errorMessage?: string;
    jagToken?: string;
  }>;
}

export default function TokenSidebar({ isOpen, onClose }: TokenSidebarProps) {
  const authState = authService.getState();
  const [revealedTokens, setRevealedTokens] = useState<Set<string>>(new Set());

  const { data: sessionData } = useQuery<SessionData>({
    queryKey: ["/api/auth/sessions"],
    enabled: authState.isAuthenticated,
  });

  // Get current session tokens only - clear stale tokens on auth state change
  useEffect(() => {
    if (!authState.isAuthenticated) {
      // Clear all tokens when logged out
      localStorage.removeItem('jag_token');
      localStorage.removeItem('application_token');
      localStorage.removeItem('atlas_id_token');
    }
  }, [authState.isAuthenticated]);

  const currentSession = sessionData?.sessions?.find(s => s.application === 'jarvis');
  const jagToken = localStorage.getItem('jag_token') || '';
  const applicationToken = localStorage.getItem('application_token') || '';
  const hasJagToken = jagToken.length > 0;
  const hasApplicationToken = applicationToken.length > 0;

  const toggleTokenReveal = (sessionId: string) => {
    const newRevealed = new Set(revealedTokens);
    if (newRevealed.has(sessionId)) {
      newRevealed.delete(sessionId);
    } else {
      newRevealed.add(sessionId);
    }
    setRevealedTokens(newRevealed);
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
  };

  const getFullIdToken = () => {
    return localStorage.getItem('atlas_id_token') || '';
  };

  const getJagToken = () => {
    return localStorage.getItem('jag_token') || '';
  };

  const getApplicationToken = () => {
    return localStorage.getItem('application_token') || '';
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString("en-US", {
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      month: "short",
      day: "numeric",
      hour: "numeric",
      minute: "2-digit",
    });
  };

  const getAppDisplayName = (app: string) => {
    const names = {
      inventory: "Atlas Beverages",
      jarvis: "Jarvis AI",
    };
    return names[app as keyof typeof names] || app;
  };

  return (
    <>
      {/* Overlay */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black/20 z-40"
          onClick={onClose}
        />
      )}

      {/* Sidebar */}
      <div 
        className={`fixed right-0 top-16 h-full w-80 bg-white shadow-xl border-l border-gray-200 transform transition-transform duration-300 z-50 ${
          isOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
        data-testid="sidebar-tokens"
      >
        <div className="p-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold text-gray-900 flex items-center">
              <Key className="w-5 h-5 mr-2" />
              Authentication Tokens
            </h3>
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={onClose}
              data-testid="button-close-sidebar"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>
        </div>

        <div className="p-4 space-y-4 overflow-y-auto h-full pb-20">
          {/* Current Session */}
          {authState.user && (
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-sm">Current Session</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2 text-sm">
                <div>
                  <span className="font-medium">User:</span> 
                  <span className="ml-2" data-testid="text-current-user">
                    {authState.user.email}
                  </span>
                </div>
                <div>
                  <span className="font-medium">Status:</span> 
                  <Badge variant="secondary" className="ml-2 bg-green-100 text-green-800">
                    Active
                  </Badge>
                </div>
                {authState.session && (
                  <div>
                    <span className="font-medium">Expires:</span> 
                    <span className="ml-2" data-testid="text-session-expires">
                      {formatDate(authState.session.expiresAt)}
                    </span>
                  </div>
                )}
              </CardContent>
            </Card>
          )}

          {/* ID Token */}
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">ID Token</CardTitle>
            </CardHeader>
            <CardContent className="space-y-2 text-sm">
              <div>
                <span className="font-medium">Type:</span> 
                <span className="ml-2">ID Token (OIDC)</span>
              </div>
              <div>
                <span className="font-medium">Status:</span> 
                <Badge variant="secondary" className="ml-2 bg-blue-100 text-blue-800">
                  {authState.user ? "Active" : "Missing"}
                </Badge>
              </div>
              {getFullIdToken() && (
                <div className="bg-gray-100 p-2 rounded text-xs break-all font-mono">
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-gray-600">ID Token:</span>
                    <div className="flex gap-1">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => toggleTokenReveal('id')}
                        className="h-6 w-6 p-0"
                        data-testid="button-reveal-id-token"
                      >
                        {revealedTokens.has('id') ? (
                          <EyeOff className="w-3 h-3" />
                        ) : (
                          <Eye className="w-3 h-3" />
                        )}
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => copyToClipboard(getFullIdToken())}
                        className="h-6 w-6 p-0"
                        data-testid="button-copy-id-token"
                      >
                        <Copy className="w-3 h-3" />
                      </Button>
                    </div>
                  </div>
                  <span data-testid="text-id-token">
                    {revealedTokens.has('id') ? getFullIdToken() : getFullIdToken().substring(0, 20) + '...'}
                  </span>
                </div>
              )}
            </CardContent>
          </Card>

          {/* JAG Token - Only show after obtained */}
          {hasJagToken && (
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-sm">JAG Token (Okta Cross-App)</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2 text-sm">
                <div>
                  <span className="font-medium">Type:</span> 
                  <span className="ml-2">urn:ietf:params:oauth:token-type:id-jag</span>
                </div>
                <div>
                  <span className="font-medium">Purpose:</span> 
                  <Badge variant="secondary" className="ml-2 bg-orange-100 text-orange-800">
                    Authorization to Inventory App
                  </Badge>
                </div>
                <div className="bg-gray-100 p-2 rounded text-xs break-all font-mono">
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-gray-600">JAG Token:</span>
                    <div className="flex gap-1">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => toggleTokenReveal('jag')}
                        className="h-6 w-6 p-0"
                        data-testid="button-reveal-jag-token"
                      >
                        {revealedTokens.has('jag') ? (
                          <EyeOff className="w-3 h-3" />
                        ) : (
                          <Eye className="w-3 h-3" />
                        )}
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => copyToClipboard(getJagToken())}
                        className="h-6 w-6 p-0"
